"""
Checking dependencies for program
"""

from spman.pkgs import Pkgs

from spman.maindata import MainData


def check_main_deps():
    """
    Checking dependencies for program:
        - GNU coreutils
        - GNU diffutils
        - GNU wget
    """
    deps = {
        'coreutils': 'a/',
        'diffutils': 'ap/',
        'wget': 'n/'
    }

    pkgs = Pkgs()
    meta = MainData()
    arch = '64' if meta.arch == 'x86_64' else ''
    path = (meta.repo_dict['slack'] +
            'slackware{0}-{1}/slackware{0}/').format(arch, meta.os_version)

    for dep in deps:
        if not pkgs.find_pkgs_on_system(dep):
            print(('{0}You need to install the package '
                   '{1}{2}{3}\n{4}{5}').format(meta.clrs['lred'],
                                               meta.clrs['lcyan'],
                                               dep,
                                               meta.clrs['reset'],
                                               path,
                                               deps[dep]))
            raise SystemExit
