"""
main.py
"""

import sys

from spman.helpmess import show_help_mess


def main():
    """
    parse arguments and launch of the relevant options
    """
    # list of arguments
    # delete arguments[0] (path and name of the script)
    args = sys.argv[1:]

    # program is run without arguments
    if not len(args):
        show_help_mess('error')
        raise SystemExit
