"""
Main data for program
"""

from os import uname


class MainData(object):
    """
    Main data for program
    """
    def __init__(self):
        self.pkgs_installed_path = '/var/log/packages/'
        self.configs_path = '/home/myrequiem/projects/git/spman/etc/spman/'
        self.repo_dict = self.get_repo_dict()
        self.arch = uname()[4]
        self.os_version = self.get_os_version()

        self.clrs = {
            'red': '\x1b[31m',
            'lred': '\x1b[1;31m',
            'green': '\x1b[32m',
            'lgreen': '\x1b[1;32m',
            'yellow': '\x1b[33m',
            'lyellow': '\x1b[1;33m',
            'blue': '\x1b[34m',
            'lblue': '\x1b[1;34m',
            'magenta': '\x1b[35m',
            'lmagenta': '\x1b[1;35m',
            'cyan': '\x1b[36m',
            'lcyan': '\x1b[1;36m',
            'grey': '\x1b[37m',
            'reset': '\x1b[0m'
        }

    def get_repo_dict(self):
        """
        return dict: {'repo_name': 'url', ...} from config repo-list
        """
        repo_dict = {}
        with open(self.configs_path + 'repo-list') as config:
            for line in config:
                if not line.startswith('#') and line != '\n':
                    parts = line.split()
                    repo_dict[parts[0].strip()] = parts[1].strip()

        return repo_dict

    @staticmethod
    def get_os_version():
        """
        docstring
        """
        return open('/etc/slackware-version').read().split()[1]
