"""
Manage queue for 'sbo' repository
"""

from spman.maindata import MainData


class Queue(object):
    """
    Manage queue for 'sbo' repository
    """
    def __init__(self):
        self.meta = MainData()

    def clear(self):
        """
        clear queue
        """
        print(self)

    def show(self):
        """
        print queue
        """

    def install(self):
        """
        download, build and install package(s) on the queue
        """
        print(self)

    def add(self, pkgs):
        """
        add package(s) in the queue
        """
        print(pkgs)
        print(self)

    def remove(self, pkgs):
        """
        remove package(s) from the queue
        """
        print(pkgs)
        print(self)
