"""
Show information about all repositories.
"""

from spman.maindata import MainData
from spman.getrepodata import GetRepoData


class ShowInfoRepos(object):
    """
    Show information about all repositories.
    """
    def __init__(self):
        self.meta = MainData()
        self.repodata = GetRepoData()

    def start(self):
        """
        init show information
        """
        repos = self.meta.get_repo_dict()
        for repo in sorted(repos):
            rdata = self.repodata.start(repo)
            print('============= ' + repo + ' =============')
            print('Last updated: {0}'.format(rdata['lupd']))
            if repo == 'sbo':
                print('vlc')
                print(rdata['pkgs']['vlc'])
            elif repo == 'multilib':
                print('alsa-oss-compat32')
                print(rdata['pkgs']['alsa-oss-compat32'])
            elif repo == 'alienbob':
                print('chromium')
                print(rdata['pkgs']['chromium'])
            else:
                print('php')
                print(rdata['pkgs']['php'])

            print()

    def asdf(self):
        """
        docstring
        """
        print(self)
