"""
Utils
"""

from os import (
    path,
    remove
)
from subprocess import call

from spman.maindata import MainData


def get_line(char, length):
    """
    return line
    """
    return char * length


def get_indent(width1, width2):
    """
    get space indent for format print
    """
    return ' ' * (width2 - width1)


def download(dfile, url, prefix, wgetparam='', show_process=True):
    """
    download file/dir
    """
    if path.isfile(dfile):
        remove(dfile)

    meta = MainData()
    if show_process:
        print('{0}Downloading:{1} {2}'.format(meta.clrs['lyellow'],
                                              meta.clrs['reset'],
                                              url))

    wgetprm = meta.get_spman_conf()['WGET_OPT']
    if wgetparam:
        wgetprm = wgetparam

    call('wget {0} --directory-prefix={1}/ {2}'.format(wgetprm,
                                                       prefix,
                                                       url),
         shell=True)

    if not path.isfile(dfile):
        print('{0}Failed download file !!!{1}'.format(meta.clrs['red'],
                                                      meta.clrs['reset']))
